import React, { useState, useEffect } from "react";
import Layout from './Layout';
import {
    getProducts,
    getBraintreeClientToken,
    processPayment
} from './apiCore'
import { emptyCart }from './cartHelpers';
import Card from './Card';
import { isAuthenticated } from "../auth";
import { Link } from 'react-router-dom';
import DropIn from 'braintree-web-drop-in-react';


const Checkout = ({products, setRun = f => f, run = undefined }) => {

  const [data, setData] = useState({
    loading: false,
    success: false,
    clientToken: null,
    error: '',
    instance: {},
    address: ''
  })

  const userId = isAuthenticated() && isAuthenticated().user._id;
  const token = isAuthenticated() && isAuthenticated().token

  const getToken = (userId, token) => {
    getBraintreeClientToken(userId, token).then(data => {
      if(data.error) {
        setData({...data, error: data.error})
      }
      else {
        setData({ ...data, clientToken: data.clientToken})
      }
    })
  }

  useEffect(() => {
    getToken(userId, token)
  }, []);


  const getTotal = () => {
      return products.reduce((currentValue, nextValue) => {
        return currentValue + nextValue.count * nextValue.price;
      }, 0);
  }

  const showCheckout = () => {
      return isAuthenticated() ? (
          <div>{showDropIn()}</div>
        ) : (
          <Link to="/signin">
            <button className="btn btn-primary">
              Sign into checkout
            </button>
          </Link>
      );
  };

  const buy = () => {
    //send the nonce to your Server
    setData({ loading: true });
    console.log("Inside Buy Method");
    let nonce;
    let getNonce = data.instance.requestPaymentMethod().then(data => {
      console.log(data);
      nonce = data.nonce;
      // once you have nonce (card type, card number) send nonce as 'paymentMethodNonce'
      // and also total to be charged
      console.log("send nonce total to process: ", nonce, getTotal(products));
      const paymentData = {
        paymentMethodNonce: nonce,
        amount: getTotal(products)
      };

      processPayment(userId, token, paymentData)
          .then(response => {
            console.log(response);
            setData({...data, success: response.success });
            //empty cart
            emptyCart(() => {
              setRun(!run); // run useEffect in parent Cart
              console.log("payment success and empty cart");
              setData({
                  loading: false,
                  success: true
              });
            });
            //create order
          })
          .catch(error => {
            console.log(error);
            setData({ loading: false });
          })
    })
    .catch(error => {
      console.log('dropin error: ', error)
      setData({...data, error: error.message })
    });
  };

  const handleAddress = event => {
          setData({ ...data, address: event.target.value });
      };




  let deliveryAddress = data.address;

  const showDropIn = () => (
    <div onBlur={() => setData({ ...data, error: "" })}>
            {data.clientToken != null && products.length > 0 ?
            (
                <div className="gorm-group mb-3">
                <label className="text-muted">Delivery address:</label>
                <textarea
                    onChange={handleAddress}
                    className="form-control"
                    value={data.address}
                    placeholder="Type your delivery address here..."
                />
                   <DropIn options={{ authorization: data.clientToken }} onInstance={instance => (data.instance = instance)} />
                  <button onClick={buy} className="btn btn-success btn-block">Pay</button>
                </div>
            ) : null}
    </div>
  );

  const showError = error => (
    <div
      className="alert alert-danger"
      style={{ display: error ? "": "none" }}
    >
    {error}
    </div>
  );

  const showSuccess = success => (
    <div
      className="alert alert-info"
      style={{ display: success ? "": "none" }}
    >
    Thanks! Your payment was successful!
    </div>
  );

  const showLoading = loading => loading && <h2>Loading...</h2>;

  return (
      <div>
      <h2>Total: ${getTotal()}</h2>
      {showLoading(data.loading)}
      {showSuccess(data.success)}
      {showError(data.error)}
      {showCheckout()}
      </div>
  ) ;
};

export default Checkout;
