import React, { useState } from "react";
import { Redirect } from "react-router-dom";
import Layout from '../core/Layout';
import {API} from '../config';
import {signin, authenticate, isAuthenticated } from '../auth';

const Signin = () => {
  const [values, setValues] = useState({
    email: 'tester19@gmail.com',
    Password: 'abc123',
    error: '',
    loading: false,
    redirectToReferrer: false,
  })

  const { email, password, loading, error, redirectToReferrer } = values
  const { user } = isAuthenticated()

  const handleChange = name => event => {
    setValues({ ...values, error: false, [name]: event.target.value });
  }



  const clickSubmit = (event) => {
    console.log("I m here");
    event.preventDefault();
    setValues({...values, error: false, loading: true})
    signin({ email, password })
    .then(data => {
      console.log("data");
      console.log(data);
      if(data.error) {
        setValues({ ...values, error: data.error, loading: false })
      }
      else {
        authenticate(data, () => {
          setValues({
            ...values,
            redirectToReferrer: true
          }); // setValues
        }); // authenticate
      } // else
    })

  }

  const signInForm = () => (
    <form>

      <div className="form-group">
        <label className="text-muted">Email</label>
        <input
          onChange={handleChange('email')}
          type="email"
          className="form-control"
          value={email}
        />
      </div>

      <div className="form-group">
        <label className="text-muted">Password</label>
        <input
          onChange={handleChange('password')}
          type="password"
          className="form-control"
          value={password}
        />
      </div>

      <button onClick={clickSubmit} className="btn btn-primary">Submit</button>
    </form>
  )

  const showError = () => (<div className="alert alert-danger" style={{display: error ? '' : 'none'} }>{error}</div>);

  const showLoading = () => loading && (<div className="alert alert-info"><h2>Loading...</h2></div>);

  // const redirectUser = () => {
  //   if(redirectToReferrer) {
  //     return <Redirect to="/" />;
  //   }
  // };

  const redirectUser = () => {
    if(redirectToReferrer) {
      if (user && user.role === 1) {
        return <Redirect to="/admin/dashboard" />
      }else {
        return <Redirect to="/user/dashboard" />
      }
    }
    if (isAuthenticated()) {
      return <Redirect to="/" />;
    }
  }

  return (
    <Layout title="Sign In Page" description="Sign In to React E-Commerce App">
      {showLoading()}
      {showError()}
      { signInForm() }
      { redirectUser() }
      {JSON.stringify(values)}
    </Layout>
  )
};

export default Signin;
